//boton guardar
    private void BotonGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BotonGuardarActionPerformed
        //validar si un campo esta vacio sacado de:
        //https://www.youtube.com/watch?v=jDk3dN3ysp4&ab_channel=C%C3%B3digoXtremo
        //if else para validar que el usuario ingrese los datos necesarios
        if (textoID.getText().trim().isEmpty()) {
            JOptionPane.showMessageDialog(null, "Por favor ingresar ID", "Error", JOptionPane.WARNING_MESSAGE);
        } else if (textoCedula.getText().trim().isEmpty()) {
            JOptionPane.showMessageDialog(null, "Por favor ingresar Cedula", "Error", JOptionPane.WARNING_MESSAGE);
        } else if (textoNombre.getText().trim().isEmpty()) {
            JOptionPane.showMessageDialog(null, "Por favor ingresar Nombre", "Error", JOptionPane.WARNING_MESSAGE);
        } else if (textoDireccion.getText().trim().isEmpty()) {
            JOptionPane.showMessageDialog(null, "Por favor ingresar Direccion", "Error", JOptionPane.WARNING_MESSAGE);
        } else if (textoCorreo.getText().trim().isEmpty()) {
            JOptionPane.showMessageDialog(null, "Por favor ingresar Correo", "Error", JOptionPane.WARNING_MESSAGE);
        } else if (textoFecha.getDate() == null) {
            JOptionPane.showMessageDialog(null, "Por favor ingresar Fecha", "Error", JOptionPane.WARNING_MESSAGE);
        } else if (textoNacionalidad.getText().trim().isEmpty()) {
            JOptionPane.showMessageDialog(null, "Por favor ingresar Nacionalidad", "Error", JOptionPane.WARNING_MESSAGE);
        } else if (textoEstado.getText().trim().isEmpty()) {
            JOptionPane.showMessageDialog(null, "Por favor ingresar Estado", "Error", JOptionPane.WARNING_MESSAGE);
        } else if (textoObjetivo.getText().trim().isEmpty()) {
            JOptionPane.showMessageDialog(null, "Por favor ingresar Objetivo", "Error", JOptionPane.WARNING_MESSAGE);

        } else {
            GuardarDatos();
            JOptionPane.showMessageDialog(null, "Guardado con exito");
        }
    }//GEN-LAST:event_BotonGuardarActionPerformed
