//Boton guardar
    public void GuardarDatos() {
        //atributos
        DatosPersonales datosPersonales = new DatosPersonales();
        DatosPersonalesTXT datosPersonalesTXT = new DatosPersonalesTXT(datosPersonales);
//asignar los datos recolectados por los textField a los atritutos de la clase DatosPersonales
        datosPersonales.setId(textoID.getText());
        datosPersonales.setCedula(textoCedula.getText());
        datosPersonales.setNombre(textoNombre.getText());
        datosPersonales.setDireccion(textoDireccion.getText());
        datosPersonales.setCorreo(textoCorreo.getText());
        datosPersonales.setFechaNacimiento(textoFecha.getDate());
        datosPersonales.setNacionalidad(textoNacionalidad.getText());
        datosPersonales.setEstadoCivil(textoEstado.getText());
        datosPersonales.setObjetivoProfeccional(textoObjetivo.getText());
        
        datosPersonalesTXT.crearArchivo();//llamado de la funcion
        datosPersonalesTXT.AgregarDatos();//llamado de la funcion
    }

